    vowel_freq = {'A': 0, 'E': 0, 'I': 0, 'O': 0, 'U': 0}

    for word in words:
        for character in word:
            if character in vowel_freq:
                vowel_freq[character] += 1

    max_freq = max(vowel_freq.values())
