use std::fs;
use std::io;
use std::path::Path;

fn generate_shell_completions(app: &str, shells: &[&str]) -> Result<(), String> {
    for &shell in shells {
        match shell {
            "Bash" => generate_bash_completions(app)?,
            "Zsh" => generate_zsh_completions(app)?,
            "Fish" => generate_fish_completions(app)?,
            _ => return Err(format!("Unsupported shell: {}", shell)),
        }
    }
    Ok(())
}

fn generate_bash_completions(app: &str) -> Result<(), String> {
    let completions = format!("{}_completions.bash", app);
    let content = format!(
        r#"
# Bash completions for {}
_{}_complete() {{
    local cur prev opts
    COMPREPLY=()
    cur="${{COMP_WORDS[COMP_CWORD]}}"
    prev="${{COMP_WORDS[COMP_CWORD-1]}}"
    opts="option1 option2 option3"  # Replace with actual options
    COMPREPLY=($(compgen -W "${{opts}}" -- $cur))
    return 0
}}
complete -F _{}_complete {}
"#,
        app, app, app, app
    );
    write_completion_file(&completions, &content)
}

fn generate_zsh_completions(app: &str) -> Result<(), String> {
    let completions = format!("{}_completions.zsh", app);
    let content = format!(
        r#"
# Zsh completions for {}
_{}_complete() {{
    local -a opts
    opts=("option1" "option2" "option3")  # Replace with actual options
    _describe 'values' opts
}}
compdef _{}_complete {}
"#,
        app, app, app
    );
    write_completion_file(&completions, &content)
}

fn generate_fish_completions(app: &str) -> Result<(), String> {
    let completions = format!("{}_completions.fish", app);
    let content = format!(
        r#"
# Fish completions for {}
function _{}_complete
    set -l opts option1 option2 option3  # Replace with actual options
    complete -c {} -a "($opts)"
end
"#,
        app, app, app
    );
    write_completion_file(&completions, &content)
}

fn write_completion_file(filename: &str, content: &str) -> Result<(), String> {
    let path = Path::new(filename);
    match fs::write(path, content) {
        Ok(_) => Ok(()),
        Err(e) => Err(format!("Failed to write completion file {}: {}", filename, e)),
    }
}
```
In the solution, the `generate_shell_completions` function iterates through the specified shell environments and calls the corresponding shell-specific completion generation functions. Each shell-specific function generates the completion script content and writes it to a file with the appropriate extension. If any error occurs during the generation or writing process, an error message is returned. If successful, the function returns `Ok(())`.

