def is_prime(n):
    if n == 1:
        return False
    if n == 2:
        return True
    if n % 2 == 0:
        return False
    i = 3
    while i * i <= n:
        if n % i:
            return False
        i += 2
    return True

def solve(limit):
    result = 0
    for n in range(1, limit + 1):
        for d in range(1, int(n**0.5) + 1): 
