class Node:
    def __init__(self, data):
        self.data = data
        self.next = None

class StudyQueue:
    def __init__(self):
        self.front = None
        self.rear = None

    def enqueue(self, item):
        new_node = Node(item)
        if self.rear is None:
            self.front = new_node
            self.rear = new_node
        else:
            self.rear.next = new_node
            self.rear = new_node

    def dequeue(self):
        if self.front is None:
            return None
        item = self.front.data
        if self.front == self.rear:
            self.front = None
            self.rear = None
        else:
            self.front = self.front.next
        return item

    def peek(self):
        if self.front is None:
            return None
        return self.front.data

    def isEmpty(self):
        return self.front is None
```

