class Graph:
    def __init__(self):
        self.adjacency_list = {}

    def add_vertex(self, vertex):
        if vertex not in self.adjacency_list:
            self.adjacency_list[vertex] = []

    def add_edge(self, vertex1, vertex2):
        if vertex1 in self.adjacency_list and vertex2 in self.adjacency_list:
            self.adjacency_list[vertex1].append(vertex2)
            self.adjacency_list[vertex2].append(vertex1)

    def remove_vertex(self, vertex):
        if vertex in self.adjacency_list:
            del self.adjacency_list[vertex]
            for adj_list in self.adjacency_list.values():
                if vertex in adj_list:
                    adj_list.remove(vertex)

    def remove_edge(self, vertex1, vertex2):
        if vertex1 in self.adjacency_list and vertex2 in self.adjacency_list:
            if vertex2 in self.adjacency_list[vertex1]:
                self.adjacency_list[vertex1].remove(vertex2)
            if vertex1 in self.adjacency_list[vertex2]:
                self.adjacency_list[vertex2].remove(vertex1)

    def has_vertex(self, vertex):
        return vertex in self.adjacency_list

    def has_edge(self, vertex1, vertex2):
        return vertex1 in self.adjacency_list and vertex2 in self.adjacency_list[vertex1]

    def print_graph(self):
        for vertex, adj_list in self.adjacency_list.items():
            print(f"{vertex} -> {adj_list}")


