```python
def get_byte_size(obj, seen=None, size_of_other_types=None):
    if seen is None:
        seen = set()
    if size_of_other_types is None:
        size_of_other_types = lambda o: 0

    try:
        byte_size = obj.__sizeof__()
    except AttributeError:
        try:
            byte_size = size_of_other_types(obj)
        except TypeError:
            raise TypeError("Can't obtain byte size of object of type {}".format(type(obj)))

    if id(obj) in seen:
        return 0

    seen.add(id(obj))

    if isinstance(obj, (tuple, list, set, dict)):
        for element in obj:
            byte_size += get_byte_size(element, seen, size_of_other_types)
    elif isinstance(obj, (str, int, float, bool)):
        byte_size = 0
    elif hasattr(obj, '__dict__'):
        byte_size += get_byte_size(obj.__dict__, seen, size_of_other_types)
    elif callable(obj):
        byte_size += get_byte_size(obj.__code__, seen, size_of_other_types)
    elif isinstance(obj, (type, types.ModuleType)):
        byte_size += sum(get_byte_size(value, seen, size_of_other_types) for value in obj.__dict__.values())

    return byte_size
